$(function(){countDown("2016/06/30 12:00:00",".time-count")});function countDown(d,b){var g=$(b).find(".day");var e=$(b).find(".hour");var i=$(b).find(".minute");var h=$(b).find(".second");var c=new Date(d).getTime(),f=(c-new Date().getTime())/1000;var a=setInterval(function(){if(f>1){f-=1;var k=Math.floor((f/3600)/24);var j=Math.floor((f/3600)%24);var m=Math.floor((f/60)%60);var l=Math.floor(f%60);g&&$(g).text(k);$(e).text(j<10?"0"+j:j);$(i).text(m<10?"0"+m:m);$(h).text(l<10?"0"+l:l)}else{clearInterval(a)}},1000)};